package rpc1;

import static com.jayway.restassured.RestAssured.given;
import static org.junit.Assert.*;

import java.util.HashMap;
import java.util.Map;

import org.junit.FixMethodOrder;
import org.junit.Test;
import org.junit.runners.MethodSorters;

import com.jayway.restassured.path.json.JsonPath;
import com.jayway.restassured.response.Response;

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
public class Test_Mongo_End_Points {

	@Test
	public void AbasicMongoPingTest(){
		given().when().get("/media/1")
			.then().statusCode(200);
	}
	
	@Test
    public void BbasicCreateItem() {
        Map<String,String> media = new HashMap<>();
        media.put("id", "1");
        media.put("item_Id", "1");
        media.put("fileName", "Mushroom");
        media.put("filePath", "data:image/jpeg;base64,");

        // Encode filePath and assert it
        
        Response r = given()
	        .contentType("application/json")
	        .body(media)
	        .when().post("/media").then()
	        .statusCode(200)
	        .and()
	        .extract().response();

        JsonPath test = r.jsonPath();

        assertEquals(1, test.getInt("item_Id"));
        assertEquals("Mushroom", test.getString("fileName"));
        assertEquals("", test.getString("filePath"));
        System.out.println("Create Media");
        System.out.println("Item ID: 1 == " + test.getInt("item_Id"));
        System.out.println("File Name: Mushroom == " + test.getString("fileName"));
        System.out.println("File Path: == " + test.getString("filePath"));
    }
	
	@Test
    public void CbasicGetMediaFile() {
        Response r = given().pathParam("id", "1")
        	.when().get("/media/single/{id}")
            .then().statusCode(200)
            .and()
            .extract().response();

        JsonPath test = r.jsonPath();

        assertEquals("data:image/jpeg;base64,", test.getString("filePath"));
        System.out.println("File Path by ID");
        System.out.println("File Path: == " + test.getString("filePath"));
    }
	
	@Test
	public void DbasicModifyMedia() {
		Map<String,String> media = new HashMap<>();
		media.put("id", "1");
        media.put("item_Id", "2");
        media.put("fileName", "1-UP Mushroom");
        media.put("filePath", "data:image/jpeg;base64,");
        
        Response r = given()
	        .contentType("application/json")
	        .body(media)
	        .when().put("/media").then()
	        .statusCode(200)
	        .and()
	        .extract().response();

       JsonPath test = r.jsonPath();
       
       assertEquals(2, test.getInt("item_Id"));
       assertEquals("1-UP Mushroom", test.getString("fileName"));
       assertEquals("", test.getString("filePath"));
       System.out.println("Modify Media");
       System.out.println("Item ID: 2 == " + test.getInt("item_Id"));
       System.out.println("File Name: 1-UP Mushroom == " + test.getString("fileName"));
       System.out.println("File Path: == " + test.getString("filePath"));
	}
	
	@Test
    public void EbasicGetMediaById() {
		Response r = given().pathParam("id", 1)
	    	.when().get("/media/{id}")
	        .then().statusCode(200)
            .and()
            .extract().response();
		
        JsonPath test = r.jsonPath();

        assertEquals(2, test.getInt("item_Id"));
        assertEquals("1-UP Mushroom", test.getString("fileName"));
        assertEquals("", test.getString("filePath"));
        System.out.println("Get Media by ID");
        System.out.println("Item ID: 2 == " + test.getInt("item_Id"));
        System.out.println("File Name: 1-UP Mushroom == " + test.getString("fileName"));
        System.out.println("File Path: == " + test.getString("filePath"));
    }

	@Test
    public void FbasicDeleteMedia() {
		Response r = given().pathParam("id", 1)
	        .when().delete("/media/{id}")
	        .then().statusCode(200)
            .and()
            .extract().response();

        assertEquals("", r.asString());
        System.out.println("Delete Media");
        System.out.println("Media == " + r.asString());
    }
}
